// Check if workspace has exceeded API limits
const { workspaceId } = $vars;
const apiLimit = await checkApiLimit(workspaceId);
if (apiLimit.exceeded) {
  return { action: 'BLOCKED', reason: 'API_LIMIT_EXCEEDED' };
}
return { action: 'CONTINUE' };

// Check for spam behavior (10+ messages in 30 seconds)
const { phoneNumber, workspaceId } = $vars;
const spamCheck = await checkSpamBehavior(phoneNumber, workspaceId);
if (spamCheck.isSpam) {
  await addToAutoBlacklist(phoneNumber, workspaceId, 'AUTO_SPAM');
  return { action: 'BLOCKED', reason: 'SPAM_DETECTED' };
}
return { action: 'CONTINUE' };

// Check if user is blacklisted
const { phoneNumber, workspaceId } = $vars;
const isBlacklisted = await checkBlacklist(phoneNumber, workspaceId);
if (isBlacklisted) {
  return { action: 'BLOCKED', reason: 'BLACKLISTED' };
}
return { action: 'CONTINUE' };

// Detect if new user or existing user
const { phoneNumber, workspaceId, message } = $vars;
const customer = await getCustomer(phoneNumber, workspaceId);

if (!customer) {
  // New user - check for greeting
  const greetings = ['ciao', 'hello', 'hi', 'hola', 'buongiorno', 'good morning'];
  const isGreeting = greetings.some(g => message.toLowerCase().includes(g));

  if (isGreeting) {
    return { action: 'WELCOME_NEW', customer: null };
  } else {
    return { action: 'WELCOME_NEW', customer: null };
  }
} else {
  // Existing user - check last conversation time
  const lastMessage = await getLastMessage(phoneNumber, workspaceId);
  const twoHoursAgo = new Date(Date.now() - 2 * 60 * 60 * 1000);

  if (!lastMessage || new Date(lastMessage.createdAt) < twoHoursAgo) {
    return { action: 'WELCOME_BACK', customer };
  } else {
    return { action: 'CONTINUE_CHAT', customer };
  }
}
