const item = $input.first();

let message = ''; 

const customerLanguage = item.json?.customer?.language;
const language = customerLanguage || 'en';

console.log("Detected Language:", language);

 JSON.stringify(item.json?.wipMessages, null, 2);

if (language == 'it') {
  message = item.json?.precompiledData.businessInfo.wipMessages?.it;
} else if (language == 'es') {
  message = item.json?.precompiledData.businessInfo.wipMessages?.es;
} else if (language == 'en') {
 message = item.json?.precompiledData.businessInfo.wipMessages?.en;
} else if (language == 'pt') {
 message = item.json?.precompiledData.businessInfo.wipMessages?.pt;
} else {
  message = item.json?.precompiledData.businessInfo.wipMessages?.en
}


return [
  {
    json: {
      message: message
    }
  }
];

