const item = $input.first();

let message =item.json.messageContent || "";


return [
  {
    json: {
      message: message
    }
  }
];

const agentConfig =$input.first().json.original.body.precompiledData.agentConfig;
 const messages = $input.first().json.original.body.precompiledData.customer.conversationHistory;

const messageContent= $input.first().json.bodyValue.messageContent
const sessionToken= $input.first().json.bodyValue.sessionToken
const workspaceId = $input.first().json.original.body.workspaceId
const language = $input.first().json.original.body.precompiledData.customer.language
const customerid = $input.first().json.bodyValue.precompiledData.customer.id

return [
  {
    json: {
      chatInput : messageContent,
      temperature : agentConfig.temperature,
      maxTokens: agentConfig.maxTokens,
      model: agentConfig.model,
      messages,
      messageContent,
      sessionId: sessionToken,
      language, 
      workspaceId,
      customerid
    },

  }
];
