//const agentConfig =$input.first().json.original.precompiledData.agentConfig
//const conversationHistory = $input.first().json.precompiledData.customer.conversationHistory

const messageContent= $input.first().json.messageContent

//const workspaceId = $input.first().json.original.precompiledData.agentConfig.workspaceId
const customerLanguage = $input.first().json.precompiledData.customer.language
const customerid = $input.first().json.precompiledData.customer.id

const temperature = $input.first().json.precompiledData.agentConfig.temperature
const maxTokens = $input.first().json.precompiledData.agentConfig.maxTokens
const model = $input.first().json.precompiledData.agentConfig.model
const conversationHistory = $input.first().json.precompiledData.customer.conversationHistory
const prompt = $input.first().json.precompiledData.agentConfig.prompt
const phone = $input.first().json.precompiledData.customer.phone
const sessionToken= phone
const discount = $input.first().json.precompiledData.customer.discount | 0

// üö® CRITICAL: Detect language from user message instead of customer record
function detectLanguageFromMessage(message) {
  if (!message) return 'en';

  const lowerMessage = message.toLowerCase();

  // Italian patterns
  const italianPatterns = [
    /\b(ciao|ciao|buongiorno|buonasera|grazie|prego|per favore|scusa|mi dispiace|va bene|ok|si|no)\b/i,
    /\b(ordine|ordini|prodotti|servizi|prezzi|costi|disponibile|disponibili)\b/i,
    /\b(dammi|voglio|vorrei|posso|puoi|hai|avete|quanto|quale|quali)\b/i,
    /\b(ultimo|primo|secondo|terzo|quarto|quinto)\b/i,
    /\b(lista|catalogo|menu|offerte|sconti|promozioni)\b/i
  ];

  // Spanish patterns
  const spanishPatterns = [
    /\b(hola|buenos d√≠as|buenas tardes|gracias|por favor|perd√≥n|lo siento|vale|ok|s√≠|no)\b/i,
    /\b(pedido|pedidos|productos|servicios|precios|costos|disponible|disponibles)\b/i,
    /\b(dame|quiero|me gustar√≠a|puedo|puedes|tienes|tienen|cu√°nto|cu√°l|cu√°les)\b/i,
    /\b(√∫ltimo|primero|segundo|tercero|cuarto|quinto)\b/i,
    /\b(lista|cat√°logo|men√∫|ofertas|descuentos|promociones)\b/i
  ];

  // Portuguese patterns
  const portuguesePatterns = [
    /\b(ol√°|bom dia|boa tarde|obrigado|por favor|desculpe|sinto muito|ok|sim|n√£o)\b/i,
    /\b(pedido|pedidos|produtos|servi√ßos|pre√ßos|custos|dispon√≠vel|dispon√≠veis)\b/i,
    /\b(d√°-me|quero|gostaria|posso|podes|tens|t√™m|quanto|qual|quais)\b/i,
    /\b(√∫ltimo|primeiro|segundo|terceiro|quarto|quinto)\b/i,
    /\b(lista|cat√°logo|menu|ofertas|descontos|promo√ß√µes)\b/i
  ];

  // Count matches for each language
  let italianMatches = 0;
  let spanishMatches = 0;
  let portugueseMatches = 0;

  italianPatterns.forEach(pattern => {
    if (pattern.test(lowerMessage)) italianMatches++;
  });

  spanishPatterns.forEach(pattern => {
    if (pattern.test(lowerMessage)) spanishMatches++;
  });

  portuguesePatterns.forEach(pattern => {
    if (pattern.test(lowerMessage)) portugueseMatches++;
  });

  // Return the language with most matches
  if (italianMatches > spanishMatches && italianMatches > portugueseMatches) {
    return 'it';
  } else if (spanishMatches > italianMatches && spanishMatches > portugueseMatches) {
    return 'es';
  } else if (portugueseMatches > italianMatches && portugueseMatches > spanishMatches) {
    return 'pt';
  }

  // Default to English if no clear pattern
  return 'en';
}

// Use detected language from message, fallback to customer language
const detectedLanguage = detectLanguageFromMessage(messageContent);
const language = detectedLanguage || customerLanguage || 'en';

return [
  {
    json: {
      chatInput : messageContent,
      temperature,
      maxTokens,
      model,
      messages: conversationHistory,
      sessionId: sessionToken,
      language, 
      workspaceId: $input.first().json.workspaceId,
      customerid,
      prompt,
      phone,
      detectedLanguage: detectedLanguage,
      customerLanguage: customerLanguage
    },

  }
];

const languageRaw = $input.first().json.precompiledData.customer.language || 'en';
const language = languageRaw.toLowerCase();

let messageWip = 'WIP_' + language; // fallback generico

const wipMessages = $input.first().json.wipMessages;

if (language === 'it') {
    messageWip = wipMessages.it;
} else if (language === 'es') {
    messageWip = wipMessages.es;
} else if (language === 'en') {
    messageWip = wipMessages.en;
} else if (language === 'pt') {
    messageWip = wipMessages.pt;
} else if (wipMessages[language]) {
    messageWip = wipMessages[language]; // supporto per altre lingue future
}

return [
  {
    json: {
      message: messageWip
    }
  }
];

